mapper.select:



        AND menu_name like concat('%', #{menuName}, '%')


        AND status = #{status}


order by parent_id, order_num




        AND menu_name like concat('%', #{menu.menuName}, '%')


        AND status = #{menu.status}


order by parent_id, order_num

select distinct m.menu_id, m.parent_id, m.menu_name, m.path, m.component, m.status, ifnull(m.perms,'') as perms, m.menu_type, m.order_num, m.create_time from t_menu m left join t_role_menu rm on m.menu_id = rm.menu_id left join t_user_role ur on rm.role_id = ur.role_id left join t_role ro on ur.role_id = ro.role_id where ur.user_id = #{params.userId}
AND m.menu_name like concat('%', #{menuName}, '%')
AND m.status = #{status}
order by m.parent_id, m.order_num

select count(1)
from t_menu
where parent_id = #{menuId}

mapper.update:
update t_menu
menu_name = #{menuName}, parent_id = #{parentId}, order_num = #{orderNum}, path = #{path}, component = #{component}, `query` = #{query}, is_frame = #{isFrame}, is_cache = #{isCache}, menu_type = #{menuType}, status = #{status}, perms = #{perms}, icon = #{icon}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate()
where menu_id = #{menuId}
