mapper.select:
select u.user_id, u.nick_name, u.user_name, u.email, u.avatar, u.mobile, u.sex, u.status, u.del_flag, u.login_ip, u.login_date, u.create_by, u.create_time from t_user u where u.del_flag = '0'
AND u.user_id = #{user.userId}
AND u.user_name like concat('%', #{user.userName}, '%')
AND u.status = #{user.status}
AND u.mobile like concat('%', #{user.mobile}, '%')

mapper.update:
update t_user
user_name = #{userName}, nick_name = #{nickName}, email = #{email}, mobile = #{mobile}, sex = #{sex}, avatar = #{avatar}, password = #{password}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, update_time = sysdate()
where user_id = #{userId}

update t_user
set avatar = #{avatar}
where user_name = #{userName}

update t_user
set password = #{password}
where user_name = #{userName}

mapper.delete:
update t_user
set del_flag = '2'
where user_id = #{userId}
