mapper.delete:
delete
from t_user_article_thumb
where user_id = #{userId}
  and article_id = #{articleId}

mapper.select:

where del_flag = '0'

    AND title like concat('%', #{article.title}, '%')


    AND status = #{article.status}

select article_id
from t_user_article_thumb
where user_id = #{userId}

select s.sort_id
from t_article a,
     t_article_sort s
where a.article_id = #{articleId}
  and a.article_id = s.article_id

select t.tag_id
from t_article a,
     t_article_tag t
where a.article_id = #{articleId}
  and a.article_id = t.article_id

select user_id
from t_user_article_view
where article_id = #{articleId}

mapper.update:
update t_article_sort
set sort_id = #{sortId}
where article_id = #{articleId}

update t_article
title = #{title}, summary = #{summary}, content = #{content}, author = #{author}, view_count = #{viewCount}, thumb_count = #{thumbCount}, collect_count = #{collectCount}, cover = #{cover}, status = #{status}, is_publish = #{isPublish}, sort = #{sort}, open_comment = #{openComment}, del_flag = #{delFlag}, update_by = #{updateBy}, update_time = sysdate()
where article_id = #{articleId}
